mapper.insert:
			INSERT INTO projectdb.file_community
            (
                          FILE_NO
                        , NICKNAME
                        , NOTICE_CATEGORY
                        , NOTICE_NO
                        , NOTICE_FILENAME
                        , NOTICE_FILE_PATH
                        , NOTICE_FILE_SIZE
            ) value (
            	  (SELECT 
                   		IFNULL(MAX(FILE_NO), 0) +1
                   FROM   
                   		file_community ALIAS_FOR_SUBQUERY
                   WHERE  
                   		NOTICE_NO = #{noticeNo}
                   AND    
                   		NOTICE_CATEGORY = #{noticeCategory})
		            , #{nickname}
		            , #{noticeCategory}
		            , #{noticeNo}
		            , #{noticeFileName}
		            , #{noticeFilePath}
		            , #{noticeFileSize}
            );

mapper.select:
SELECT * FROM FILE_COMMUNITY
WHERE 
	NOTICE_NO = #{noticeNo}
AND
	NOTICE_CATEGORY = #{noticeCategory}

mapper.delete:
DELETE FROM FILE_COMMUNITY
WHERE 
	NOTICE_CATEGORY = #{noticeCategory}
AND 
	NOTICE_NO = #{noticeNo}
AND
	FILE_NO = #{fileNo}

DELETE FROM FILE_COMMUNITY
WHERE
	NOTICE_CATEGORY = #{noticeCategory}
AND
	NOTICE_NO = #{noticeNo}
